(function () {
    $(function () {

        var _$dmquanhuyenTable = $('#gridContainer');
        var _dmquanhuyenService = abp.services.qlncc.danhMucQuanHuyen;

        var _createOrEditModal = new app.ModalManager({
            viewUrl: abp.appPath + 'Admin/DanhMucQuanHuyen/CreateOrEditModal',
            scriptUrl: abp.appPath + 'view-resources/Areas/Admin/DanhMucQuanHuyen/_CreateOrEditModal.js',
            modalClass: 'CreateOrEditQuanHuyenModal'
        });

        var _importFileModal = new app.ModalManager({
            viewUrl: abp.appPath + 'Admin/DanhMucQuanHuyen/ImportFileModal',
            scriptUrl: abp.appPath + 'view-resources/Areas/Admin/DanhMucQuanHuyen/FileUpload.js',
            modalClass: 'ImportQuanHuyenModal'
        });

        function editquanhuyen(data) {
            _createOrEditModal.open({ id: data.Id });
        }

        function deletequanhuyen(data) {
            abp.message.confirm(
                app.localize('AreYouSure'),
                app.localize('XacNhan'),
                function (isConfirmed) {
                    if (isConfirmed) {
                        _dmquanhuyenService.delete(data.Id).done(function (res) {
                            if (res.success == true) {
                                getquanhuyen();
                                abp.notify.success(app.localize('SuccessfullyDeleted'));
                            } else {
                                abp.notify.error(app.localize('DeletedError'));
                            }
                        });
                    }
                }
            );
        }
        var parmaforall = {};

        DevExpress.localization.locale("vi");
        function isNotEmpty(value) {
            return value !== undefined && value !== null && value !== "";
        }
        var customDataSource = new DevExpress.data.CustomStore({
            key: "id",
            load: function (loadOptions) {
                var d = $.Deferred();
                var params = {};
                [
                    "filter",
                    "group",
                    "groupSummary",
                    "parentIds",
                    "requireGroupCount",
                    "requireTotalCount",
                    "searchExpr",
                    "searchOperation",
                    "searchValue",
                    "select",
                    "sort",
                    "skip",
                    "take",
                    "totalSummary",
                    "userData"
                ].forEach(function (i) {
                    if (i in loadOptions && isNotEmpty(loadOptions[i])) {
                        params[i] = JSON.stringify(loadOptions[i]);
                    }
                });
                params.filterext = $("#filterText").val();
                params.tinhthanh = $("#TinhThanh_Ma").val();
                console.log(params);
                parmaforall = params;
                $.getJSON(abp.appPath + "api/services/qlncc/DanhMucQuanHuyen/GetAllItem", params)
                    .done(function (response) {
                        $.each(response.result.data, function (i, val) {
                            val.stt = parseInt(params.skip) + i + 1;
                        });
                        d.resolve(response.result, {
                            totalCount: response.result.totalCount,
                            summary: response.result.summary
                        });
                        setTimeout(function () {
                            $(`#gridContainer tr[class="dx-row dx-column-lines dx-datagrid-filter-row"] input[class="dx-texteditor-input"]`).prop("placeholder", "Tìm kiếm");
                        }, 500)
                    })
                    .fail(function (res) {
                        if (res.responseJSON.error.message.indexOf("Required permissions are not granted") >= 0) {
                            abp.notify.error("Bạn không có quyền!");
                            d.resolve([], {
                                totalCount: 0
                            });
                        }
                    });
                return d.promise();
            },
        });

        var dataGrid = $("#gridContainer").dxDataGrid({

            dataSource: customDataSource,
            remoteOperations: { paging: true, sorting: true, filtering: true },
            key: "id",
            showBorders: true,

            wordWrapEnabled: true,
            grouping: {
                autoExpandAll: true,
            },
            columnResizingMode: "nextColumn",
            pager: {
                allowedPageSizes: [10, 20, 50, 100],
                showInfo: true,
                showNavigationButtons: true,
                showPageSizeSelector: true,
                visible: true
            },
            paging: {
                pageSize: 10
            },
            editing: {
                mode: "row",
            },

            enterKeyAction: "moveFocus",

            filterRow: {
                visible: false
            },

            columns: [
                {
                    dataField: "stt",
                    caption: "STT",
                    width: 50,
                    allowFiltering: false,
                    allowSorting: false,
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + "</p>")
                            .appendTo(header);
                    },
                    cellTemplate: function (container, options) {
                        $(container).append(`<p style="text-align: center;margin:0;">${options.text}</p>`)
                    }
                },
                {
                    dataField: "quanHuyen_Ma",
                    caption: "Mã quận huyện",
                    width: 200,
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + " </p>")
                            .appendTo(header);
                    },
                    filterOperations: ["contains", "="],
                    selectedFilterOperation: "contains"
                },
                {
                    dataField: "quanHuyen_Ten",
                    caption: "Tên quận huyện",
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + " </p>")
                            .appendTo(header);
                    },
                    filterOperations: ["contains", "="],
                    selectedFilterOperation: "contains"
                },
                {
                    dataField: "quanHuyen_TenTat",
                    caption: "Tên tắt",
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + " </p>")
                            .appendTo(header);
                    },
                    filterOperations: ["contains", "="],
                    selectedFilterOperation: "contains"
                },
                {
                    dataField: "tinhThanh_Ten",
                    caption: "Tỉnh thành",
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + " </p>")
                            .appendTo(header);
                    },
                    filterOperations: ["contains", "="],
                    selectedFilterOperation: "contains"
                },
                
                {
                    dataField: "quanHuyen_HieuLuc",
                    caption: "Hiệu lực",
                    width: 150,
                    headerCellTemplate: function (header, info) {
                        $('<div>')
                            .html(`<p style="font-size: 14px;color:#000;margin:0;">` + info.column.caption + " </p>")
                            .appendTo(header);
                    },
                    filterOperations: ["contains", "="],
                    selectedFilterOperation: "contains"
                },
                {
                    caption: "Hành động",
                    width: 120,
                    allowSorting: false,
                    alignment: 'center',
                    cellTemplate: function (container, options) {
                        var txt = "";
                        if (abp.auth.isGranted('Admin.DanhMuc.Khac.QuanHuyen.Sua')) {
                            txt += `<a class="btn btn-sm btn-icon btn-icon-md edit" title="Chỉnh sửa" style="color:#169BD5;margin-top:-10px;" href="#" data-id="${options.data.id}"><i class="fa fa-edit icon-color"></i></a>`;
                        }
                        if (abp.auth.isGranted('Admin.DanhMuc.Khac.QuanHuyen.Xoa')) {
                            txt += `<a class="btn btn-sm btn-icon btn-icon-md delete" title="Xóa" style="color:red;margin-top:-10px" data-id="${options.data.id}" href="#"><i class="fa fa-trash icon-color"></i></a>`;
                        }
                        $(container).append(txt);
                    }
                }
            ]
        }).dxDataGrid("instance");

        function getquanhuyen() {
            $("#gridContainer").dxDataGrid("instance").refresh();
            $(".checkboxalldelete").prop("checked", false);
        }

        registerEvent();
        function registerEvent() {

            $('.combobox').selectpicker({
                iconBase: "fa",
                tickIcon: "fa fa-check"
            });

            $("#filterButton").click(function (e) {
                e.preventDefault();
                getquanhuyen();
            });

            $("#TinhThanh_Ma").on("change", function () {
                getquanhuyen();
            })

            $('#CreateButton').click(function () {
                _createOrEditModal.open();
            });

            $('#gridContainer').on('click', 'a.edit', function () {
                var data = new Object();
                data.Id = parseInt($(this).attr("data-id"));
                editquanhuyen(data);
            });
            $('#gridContainer').on('click', 'a.delete', function () {
                var data = new Object();
                data.Id = parseInt($(this).attr("data-id"));
                deletequanhuyen(data);
                getquanhuyen();
            });

            $('#DeleteButton').on('click', function () {
                abp.message.confirm(
                    app.localize('AreYouSure'),
                    app.localize('XacNhan'),
                    function (isConfirmed) {
                        if (isConfirmed) {
                            abp.ui.setBusy("body");
                            _dmquanhuyenService.done(function (res) {
                                if (res.success == true) {
                                    getquanhuyen();
                                    abp.notify.success("Xóa thành công!");
                                    $('#DeleteButton').prop("disabled", true);
                                } else {
                                    abp.notify.error(res.message);
                                }
                            }).always(function () {
                                abp.ui.clearBusy("body");
                            })
                        }
                    }
                );
            });

            abp.event.on('app.createOrEditModalSaved', function () {
                getquanhuyen();
            });

            $('#ImportExcelButton').on('click', function () {
                _importFileModal.open();
            });

            $('#filterText').keypress(function (event) {
                var keycode = (event.keyCode ? event.keyCode : event.which);
                if (keycode == '13') {
                    getquanhuyen();
                }
            });
        }

        $('#ExportExcelButton').click(function (e) {
            e.preventDefault();
            abp.ui.setBusy("body");
            _dmquanhuyenService
                .exportItem(parmaforall)
                .done(function (result) {
                    app.downloadTempFile(result);
                    abp.ui.clearBusy("body");
                });
        });
    });
})();