(function () {

    $(function () {
        var _thongtinnguoidungService = abp.services.qlncc.thongTinNguoiDung;

        $('#next-btn').click(function () {
            var userid = $("#UserId").val();
            var key = $("#Key").val();
            var code = $("#MaXacMinh").val();
            _thongtinnguoidungService.checkMaXacMinh(key, code).done(function (result) {
                if (result == true) {
                    window.location = `/Account/DoiMatKhau?userid=${userid}&&key=${key}&&code=${code}`;
                }
                else {
                    return abp.message.error('Mã xác minh không đúng');
                }
            });
        });
    });

})();